import React from "react";
import { useSelector, useDispatch } from "react-redux";
import './style.css'
import { removeReserver, updateAmountReserve } from "../../store/modules/reserve/actions";

export default function Reservas(){

    const reserves = useSelector(state => state.reserve); 
    const dispatch = useDispatch();

    function handleRemove(id){
        dispatch(removeReserver(id))
    }

    function decrementAmount(trip){
        updateAmountReserve(trip.id, trip.amount -1)
    }

    function incrementAmount(trip){
        updateAmountReserve(trip.id, trip.amount +1)
    }

    return(
        <div>
            <h1 className="title">Voce solicitou { reserves.length } reserva</h1>

            {reserves.map(reserve =>{
                return(
                    <div className="reservas" key={reserve.id}>
                        <img
                            src={reserve.image}
                            alt={reserve.title}
                        />
                        <strong>{reserve.title}</strong>
                        <div id="amount">
                            <button type="button" onClick={()=>decrementAmount(reserve)}>
                                <span>-</span>
                            </button>
                            <input type="text" readOnly value={reserve.amount}/>
                            <button type="button" onClick={()=>incrementAmount(reserve)}>
                                <span>+</span>
                            </button>
                        </div>
                        <button
                            type="button"
                            onClick={()=>handleRemove(reserve.id)}
                        >
                            <span>Deletrar</span>
                        </button>
                    </div>
                );
            })}


            <footer>
                    <button type="button">Solicitar Reservas</button>
            </footer>

        </div>
    );
}
