export function addReserve(trip){
    return{
        type: 'ADD_RESERVE',
        trip
    }
}

export function removeReserver(id){
    return{
        type: 'REMOVE_RESERVE',
        id,
    }
}

export function updateAmountReserve(id, amount){
    return{
        type: 'UPDATE_RESERVE',
        id,
        amount,
    }
}
