import { select, call, put, all, takeLatest } from 'redux-saga/effects'
import api from "../../../services/api";
import { addReserveSuccess } from './actions';

/**
 * O Reduxe Saga é um middleware 
 */

//function* é a memas coisa que async function
function* addToReserve({id}){
    const tripExist = yield select(
        state=> state.reserve.find(trip => trip.id === id)
    );

    if(tripExist){
        //Aumentar o amount
    }else{
        //yield é a mesma coisa que wait
        const response = yield call(api.get, `trips/${id}`);

        const data = {
            ...response.data,
            amount: 1,
        }

        //put funciona igual o dispatch dispara actions para os reducers
        yield put(addReserveSuccess(data))
    }


}

export default all([
    takeLatest('ADD_RESERVE_REQUEST', addToReserve)
])
