import React, {useState, useEffect} from 'react';
import {Container} from './styles'
import api from '../../services/api';

export default function Repositorio({match}){

    useEffect(()=>{
        async function load(){
            const nomeRepo = decodeURIComponent(match.parms.repositorio)

            //Ira executar 2 requições de uma vez e retornara o resultado das duas em um array
            const [repositorioData, issuesData] = await Promise.all([
                api.get(`/repos/${nomeRepo}`),
                api.get(`/repos/${nomeRepo}/issues `),
            ]);

            console.log(repositorioData);
            console.log(issuesData);
        }

        load();

    } , []);

    return(
        <Container>
            {decodeURIComponent(match.parms.repositorio)}
        </Container>
    );
}
